 3→namespace T3jet\MockupPlugin\Controller;
 4→
 5→use Shopware\Core\System\SalesChannel\SalesChannelContext;
 6→use Shopware\Storefront\Controller\StorefrontController;
 7→use Symfony\Component\HttpFoundation\Request;
 8→use Symfony\Component\HttpFoundation\Response;
 9→use Symfony\Component\Routing\Attribute\Route;
10→
11→class MockupController extends StorefrontController
12→{
13→    #[Route('/mockup', name: 'frontend.mockup.page', defaults: ['_routeScope' => ['storefront']], methods: ['GET'])]
14→    public function mockupPage(Request $request, SalesChannelContext $context): Response
15→    {
16→        $mockupType = $request->query->get('mockup', 'responsive');
17→        $url = $request->query->get('url', $request->getSchemeAndHttpHost());
18→        
19→        // Clean mockup type parameter
20→        $mockupType = strtolower(str_replace(["-", "_"], "", $mockupType));
21→        
22→        $templateData = [
23→            'mockup' => $mockupType,
24→            'url' => $url
25→        ];
26→        
27→        // Determine template based on mockup type
28→        $template = $this->getTemplateForMockupType($mockupType);
29→        
30→        return $this->renderStorefront($template, $templateData);
31→    }
32→    
33→    private function getTemplateForMockupType(string $mockupType): string
34→    {
35→        $templates = [
36→            'trible' => '@T3jetMockup/storefront/page/mockup/trible.html.twig',
37→            'quad' => '@T3jetMockup/storefront/page/mockup/quad.html.twig',
38→            'five' => '@T3jetMockup/storefront/page/mockup/five.html.twig',
39→            'six' => '@T3jetMockup/storefront/page/mockup/six.html.twig',
40→            'twelve' => '@T3jetMockup/storefront/page/mockup/twelve.html.twig',
41→            'responsive' => '@T3jetMockup/storefront/page/mockup/responsive.html.twig'
42→        ];
43→        
44→        return $templates[$mockupType] ?? $templates['responsive'];
45→    }
46→}

