/* galaxys23 Mockup Setup */
.galaxys23 .mockup {
    width: 360px;
    height: 780px;
    margin: 20px auto;
    position: relative;
    border-radius: 25px;
    padding: 20px;
    background: #000;
    box-shadow: 0 8px 30px rgba(0,0,0,0.3);
}

.galaxys23 .mockup .wrapper {
    width: 100%;
    height: 100%;
    border-radius: 15px;
    overflow: hidden;
    background: #fff;
}

.galaxys23 .mockup iframe {
    width: 100%;
    height: 100%;
    border: none;
    border-radius: 15px;
}

