/* galaxys23 Mockup Setup */
body {
    margin: 0;
    padding: 0;
    background: #f5f5f5;
    min-height: 100vh;
}

#container {
    width: 100%;
    min-height: 100vh;
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 40px 20px;
    box-sizing: border-box;
}

#container section {
    width: 100%;
    display: flex;
    align-items: center;
    justify-content: center;
}

.display {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 100%;
}

.display > div {
    max-width: none !important;
    width: auto !important;
}

.galaxys23 {
    display: flex;
    align-items: center;
    justify-content: center;
    max-width: none !important;
    width: auto !important;
}

.galaxys23 .mockup {
    width: 360px;
    height: 780px;
    position: relative;
    border-radius: 25px;
    padding: 20px;
    background: #000;
    box-shadow: 0 8px 30px rgba(0,0,0,0.3);
    margin: 20px;
    max-width: none !important;
}

.galaxys23 .mockup .wrapper {
    width: 100%;
    height: 100%;
    border-radius: 15px;
    overflow: hidden;
    background: #fff;
    position: relative;
}

.galaxys23 .mockup iframe {
    width: 100%;
    height: 100%;
    border: none;
    border-radius: 15px;
    display: block;
}

/* Responsive adjustments */
@media (max-width: 1200px) {
    .galaxys23 .mockup {
        transform: scale(0.8);
        transform-origin: center;
    }
}

@media (max-width: 900px) {
    .galaxys23 .mockup {
        transform: scale(0.6);
        transform-origin: center;
    }
}

@media (max-width: 600px) {
    .galaxys23 .mockup {
        transform: scale(0.5);
        transform-origin: center;
    }
}

