	position: relative;
	overflow: hidden;
	height: 100vh;
	width: 100vw;
	padding: 0px;
	margin: 0px;
}

.display {
	--top: 50%;
	--left: 50%;
}


.display>div{
	position: absolute;
	left: var(--left);
	top: var(--top);
	max-width: 1px;
	max-height: 1px;
}

.display .mockup {
	position: absolute;
	left: 50%;
	top: 50%;
}

.display .mockup .wrapper {
	background-repeat: no-repeat;
	background-size: 100%;
	overflow: hidden;

