        background: rgba(255, 255, 255, 0.05);
        border-radius: 50%;
        border: 2px solid #333;
    }

    .screen {
        width: 480px;
        height: 300px;
        background: #000;
        border-radius: 15px;
        overflow: hidden;
        position: relative;
        margin: 0 20px;
    }

    .screen iframe {
        width: 100%;
        height: 100%;
        border: none;
        border-radius: 15px;
    }

    .dpad {
        width: 50px;
        height: 50px;
        background: #333;
        border-radius: 8px;
        position: relative;
    }

    .buttons {
        display: grid;
        grid-template-columns: 1fr 1fr;
        grid-template-rows: 1fr 1fr;
        gap: 5px;
        width: 60px;
        height: 60px;
    }

    .button {
        background: #333;
        border-radius: 50%;
        border: 2px solid #555;
    }

