        </div>

        <div class="right-controls">
            <div class="buttons">
                <div class="button"></div>
                <div class="button"></div>
                <div class="button"></div>
                <div class="button"></div>
            </div>
            <div class="analog-stick"></div>
            <div class="trackpad"></div>
        </div>
    </div>
</div>
