    body {
        background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
        min-height: 100vh;
        display: flex;
        align-items: center;
        justify-content: center;
        padding: 20px;
        font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
    }

    .device-container {
        display: flex;
        align-items: center;
        justify-content: center;
        min-height: 100vh;
        width: 100%;
    }

    .galaxy-fold {
        width: 700px;
        height: 520px;
        position: relative;
        display: flex;
        gap: 4px;
    }

    .fold-left, .fold-right {
        width: 348px;
        height: 520px;
        background: linear-gradient(145deg, #2c2c2e, #1c1c1e);
        border-radius: 25px;
        padding: 8px;
        position: relative;
        box-shadow: 
            0 0 0 2px #4a4a4c,
            0 20px 40px rgba(0, 0, 0, 0.4),
            inset 0 1px 2px rgba(255, 255, 255, 0.1);
    }

    .fold-hinge {
        width: 4px;
        height: 520px;
        background: linear-gradient(145deg, #444, #222);
        border-radius: 2px;
        box-shadow: inset 0 0 5px rgba(0, 0, 0, 0.5);
    }

    .screen {
        width: 100%;
        height: 100%;
        background: #000;
        border-radius: 17px;
        overflow: hidden;
        position: relative;
    }

    .camera-notch {
        position: absolute;
        top: 15px;
        right: 15px;
        width: 30px;
        height: 30px;
        background: #000;
        border-radius: 50%;
        z-index: 100;
        box-shadow: 0 0 0 1px rgba(255, 255, 255, 0.1);
    }

    .screen iframe {
        width: 100%;
        height: 100%;
        border: none;
        border-radius: 17px;
    }

    .fold-crease {
        position: absolute;
        top: 0;
        right: -2px;
        width: 2px;
        height: 100%;
        background: linear-gradient(to bottom, 
            transparent 10%, 
            rgba(255, 255, 255, 0.1) 20%, 
            rgba(255, 255, 255, 0.05) 50%, 
            rgba(255, 255, 255, 0.1) 80%, 
            transparent 90%);
    }

    .fold-left .fold-crease {
        right: -2px;
    }

    @media (max-width: 800px) {
        .galaxy-fold {
            transform: scale(0.7);
        }
    }

    @media (max-width: 600px) {
        .galaxy-fold {
            transform: scale(0.5);
        }
    }
</style>
{% endblock %}

