    }

    /* Desktop */
    .desktop {
        width: 500px;
        height: 320px;
        background: linear-gradient(145deg, #2c2c2e, #1c1c1e);
        border-radius: 12px;
        padding: 8px;
        position: relative;
        box-shadow: 0 15px 30px rgba(0, 0, 0, 0.3);
    }

    .desktop .screen {
        width: 100%;
        height: 280px;
        background: #000;
        border-radius: 6px;
        overflow: hidden;
    }

    .desktop .screen iframe {
        width: 100%;
        height: 100%;
        border: none;
        border-radius: 6px;
    }

    .desktop .stand {
        width: 80px;
        height: 40px;
        background: #333;
        position: absolute;
        bottom: -20px;
        left: 50%;
        transform: translateX(-50%);
        border-radius: 0 0 8px 8px;
    }

