    .tablet {
        width: 350px;
        height: 460px;
        background: linear-gradient(145deg, #e8e8e8, #d0d0d0);
        border-radius: 25px;
        padding: 15px;
        position: relative;
        box-shadow: 0 15px 30px rgba(0, 0, 0, 0.2);
    }

    .tablet .screen {
        width: 100%;
        height: 100%;
        background: #000;
        border-radius: 12px;
        overflow: hidden;
    }

    .tablet .screen iframe {
        width: 100%;
        height: 100%;
        border: none;
        border-radius: 12px;
    }

    /* Phone */
    .phone {
        width: 200px;
        height: 420px;
        background: linear-gradient(145deg, #2c2c2e, #1c1c1e);
        border-radius: 30px;
        padding: 6px;
        position: relative;
        box-shadow: 0 15px 30px rgba(0, 0, 0, 0.3);
    }

    .phone .screen {
        width: 100%;
        height: 100%;
        background: #000;
        border-radius: 24px;
        overflow: hidden;
        position: relative;
    }

    .phone .notch {
        position: absolute;
        top: 8px;
        left: 50%;
        transform: translateX(-50%);
        width: 80px;
        height: 20px;
        background: #000;
        border-radius: 0 0 12px 12px;
        z-index: 100;
    }

    .phone .screen iframe {
        width: 100%;
        height: 100%;
        border: none;
        border-radius: 24px;
    }

    @media (max-width: 1200px) {
        .device-container {
            transform: scale(0.8);
        }
    }

    @media (max-width: 900px) {
        .device-container {
            transform: scale(0.6);
            flex-direction: column;
        }
    }

    @media (max-width: 600px) {
        .device-container {
            transform: scale(0.5);
        }
    }
</style>
{% endblock %}

