        height: 420px;
        background: linear-gradient(145deg, #e74c3c, #c0392b);
        border-radius: 8px 25px 25px 8px;
        position: relative;
        box-shadow: 
            0 15px 30px rgba(0, 0, 0, 0.3),
            inset 0 2px 4px rgba(255, 255, 255, 0.2);
    }

    .screen {
        width: 100%;
        height: 390px;
        background: #000;
        border-radius: 6px;
        overflow: hidden;
        position: relative;
    }

    .screen iframe {
        width: 100%;
        height: 100%;
        border: none;
        border-radius: 6px;
    }

    .analog-stick-left {
        position: absolute;
        top: 80px;
        left: 50%;
        transform: translateX(-50%);
        width: 40px;
        height: 40px;
        background: #333;
        border-radius: 50%;
        border: 3px solid #555;
    }

    .analog-stick-right {
        position: absolute;
        bottom: 80px;
        right: 50%;
        transform: translateX(50%);
        width: 40px;
        height: 40px;
        background: #333;
        border-radius: 50%;
        border: 3px solid #555;
    }

    .d-pad {
        position: absolute;
        bottom: 150px;
        left: 50%;
        transform: translateX(-50%);
        width: 50px;
        height: 50px;
        background: #444;
        border-radius: 8px;
    }

    .action-buttons {
        position: absolute;
        top: 150px;
        right: 50%;
        transform: translateX(50%);
        width: 50px;
        height: 50px;
        background: #444;
        border-radius: 50%;
    }

