{% extends "@DeviceMockupViewer/storefront/layout/base.html.twig" %}

{% block title %}Quad Device Mockup{% endblock %}

{% block stylesheets %}
<style>
    * {
        margin: 0;
        padding: 0;
        box-sizing: border-box;
    }

    body {
        background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
        min-height: 100vh;
        display: flex;
        align-items: center;
        justify-content: center;
        padding: 20px;
        font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
    }

    .device-container {
        display: grid;
        grid-template-columns: 1fr 1fr;
        grid-template-rows: 1fr 1fr;
        gap: 30px;
        width: 100%;
        max-width: 1200px;
        height: 800px;
        align-items: center;
        justify-items: center;
    }

    .device {
        width: 100%;
        height: 100%;
        max-width: 500px;
        max-height: 350px;
        background: linear-gradient(145deg, #2c2c2e, #1c1c1e);
        border-radius: 15px;
        padding: 12px;
        position: relative;
        box-shadow: 0 15px 30px rgba(0, 0, 0, 0.3);
    }

    .device.phone {
        max-width: 200px;
        max-height: 400px;
        border-radius: 30px;
        padding: 8px;
    }

    .device.tablet {
        max-width: 350px;
        max-height: 450px;
        border-radius: 25px;
        background: linear-gradient(145deg, #e8e8e8, #d0d0d0);
    }

    .device.desktop {
        max-width: 500px;
        max-height: 320px;
    }

    .screen {
        width: 100%;
        height: 100%;
        background: #000;
        border-radius: 8px;
        overflow: hidden;
        position: relative;
    }

    .device.phone .screen {
        border-radius: 22px;
    }

    .device.tablet .screen {
        border-radius: 17px;
    }

    .screen iframe {
        width: 100%;
        height: 100%;
        border: none;
        border-radius: inherit;
    }

    .notch {
        position: absolute;
        top: 6px;
        left: 50%;
        transform: translateX(-50%);
        width: 80px;
        height: 18px;
        background: #000;
        border-radius: 0 0 12px 12px;
        z-index: 100;
    }

    @media (max-width: 1300px) {
        .device-container {
            transform: scale(0.8);
        }
    }

    @media (max-width: 1000px) {
        .device-container {
            grid-template-columns: 1fr;
            grid-template-rows: repeat(4, 1fr);
            transform: scale(0.7);
        }
    }

    @media (max-width: 600px) {
        .device-container {
            transform: scale(0.5);
        }
    }
</style>
{% endblock %}

{% block content %}
<div class="device-container">
    <div class="device desktop">
        <div class="screen">
            <iframe src="{{ cleanUrl }}" title="Desktop View"></iframe>
        </div>
    </div>

    <div class="device tablet">
        <div class="screen">
            <iframe src="{{ cleanUrl }}" title="Tablet View"></iframe>
        </div>
    </div>

    <div class="device phone">
        <div class="screen">
            <div class="notch"></div>
            <iframe src="{{ cleanUrl }}" title="Phone View"></iframe>
        </div>
    </div>

    <div class="device desktop">
        <div class="screen">
            <iframe src="{{ cleanUrl }}" title="Laptop View"></iframe>
        </div>
    </div>
</div>
{% endblock %}

