input.content:
/* Base styles and CSS variables for device mockups */
:root {
    /* Device Colors */
    --device-dark: linear-gradient(145deg, #2c2c2e, #1c1c1e);
    --device-darker: linear-gradient(145deg, #2a2a2a, #1a1a1a);
    --device-black: linear-gradient(145deg, #1a1a1a, #0a0a0a);
    --device-border: #4a4a4c;
    --device-border-thin: #333;
    --screen-black: #000;
    --button-color: #333;
    --button-border: #555;

    /* Background */
    --bg-gradient: linear-gradient(135deg, #667eea 0%, #764ba2 100%);

    /* Shadows */
    --shadow-device: 0 25px 50px rgba(0, 0, 0, 0.5);
    --shadow-light: 0 20px 40px rgba(0, 0, 0, 0.4);
    --shadow-heavy: 0 25px 50px rgba(0, 0, 0, 0.5);
    --shadow-inset: inset 0 2px 4px rgba(255, 255, 255, 0.1);
    --shadow-inset-light: inset 0 1px 2px rgba(255, 255, 255, 0.05);

    /* Common dimensions */
    --border-radius-large: 50px;
    --border-radius-medium: 25px;
    --border-radius-small: 15px;
    --border-radius-tiny: 8px;
}

/* Reset and base styles */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    background: var(--bg-gradient);
    min-height: 100vh;
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 20px;
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
}

.device-container {
    display: flex;
    align-items: center;
    justify-content: center;
    min-height: 100vh;
    width: 100%;
}

/* Common device styles */
.device-base {
    position: relative;
    box-shadow: 
        0 0 0 3px var(--device-border),
        var(--shadow-device),
        var(--shadow-inset);
}

.device-base-thin {
    position: relative;
    box-shadow: 
        0 0 0 2px var(--device-border-thin),
        var(--shadow-light),
        var(--shadow-inset-light);
}

.screen-base {
    width: 100%;
    height: 100%;
    background: var(--screen-black);
    overflow: hidden;
    position: relative;
}

.screen-base iframe {
    width: 100%;
    height: 100%;
    border: none;
}

/* Button mixins */
.button-side {
    position: absolute;
    background: var(--device-border);
}

.button-left {
    left: -3px;
    width: 6px;
    border-radius: 3px 0 0 3px;
}

.button-right {
    right: -3px;
    width: 6px;
    border-radius: 0 3px 3px 0;
}

.button-left-thin {
    left: -2px;
    width: 4px;
    border-radius: 2px 0 0 2px;
}

.button-right-thin {
    right: -2px;
    width: 4px;
    border-radius: 0 2px 2px 0;
}

/* Camera elements */
.dynamic-island {
    position: absolute;
    top: 15px;
    left: 50%;
    transform: translateX(-50%);
    width: 125px;
    height: 37px;
    background: var(--screen-black);
    border-radius: 19px;
    z-index: 100;
    box-shadow: inset 0 0 0 1px rgba(255, 255, 255, 0.1);
}

.camera-punch {
    position: absolute;
    top: 25px;
    left: 50%;
    transform: translateX(-50%);
    width: 12px;
    height: 12px;
    background: var(--screen-black);
    border-radius: 50%;
    z-index: 100;
    box-shadow: 0 0 0 1px rgba(255, 255, 255, 0.1);
}

.camera-bar {
    position: absolute;
    top: 15px;
    left: 50%;
    transform: translateX(-50%);
    width: 120px;
    height: 30px;
    background: #1a1a1a;
    border-radius: 15px;
    z-index: 100;
    display: flex;
    align-items: center;
    justify-content: space-around;
    box-shadow: inset 0 0 0 1px rgba(255, 255, 255, 0.1);
}

.camera-lens {
    width: 8px;
    height: 8px;
    background: var(--button-color);
    border-radius: 50%;
}

/* Home indicator */
.home-indicator {
    position: absolute;
    bottom: 10px;
    left: 50%;
    transform: translateX(-50%);
    width: 140px;
    height: 5px;
    background: rgba(255, 255, 255, 0.3);
    border-radius: 3px;
    z-index: 100;
}

/* Responsive breakpoints */
@media (max-width: 1400px) {
    .scale-1400 { transform: scale(0.8); }
}

@media (max-width: 1200px) {
    .scale-1200 { transform: scale(0.8); }
}

@media (max-width: 1000px) {
    .scale-1000 { transform: scale(0.6); }
}

@media (max-width: 900px) {
    .scale-900 { transform: scale(0.6); }
}

@media (max-width: 800px) {
    .scale-800 { transform: scale(0.7); }
}

@media (max-width: 700px) {
    .scale-700 { transform: scale(0.4); }
}

@media (max-width: 600px) {
    .scale-600 { transform: scale(0.5); }
}

@media (max-width: 500px) {
    .scale-500 { transform: scale(0.3); }
}

@media (max-width: 480px) {
    .scale-480 { transform: scale(0.7); }
}

@media (max-width: 400px) {
    .scale-400 { transform: scale(0.4); }
}

@media (max-width: 360px) {
    .scale-360 { transform: scale(0.6); }
}

