input.content:
/* iPhone specific styles */

/* iPhone 15 Pro */
.iphone-15-pro {
    width: 400px;
    height: 870px;
    background: var(--device-dark);
    border-radius: var(--border-radius-large);
    padding: 8px;
}

.iphone-15-pro::before {
    content: '';
    position: absolute;
    top: 50px;
    height: 80px;
}

.iphone-15-pro::after {
    content: '';
    position: absolute;
    top: 150px;
    height: 120px;
}

.iphone-15-pro .screen {
    border-radius: 42px;
}

.iphone-15-pro .screen iframe {
    border-radius: 42px;
}

/* iPhone 15 Pro Max */
.iphone-15-pro-max {
    width: 430px;
    height: 932px;
    background: var(--device-dark);
    border-radius: 55px;
    padding: 8px;
}

.iphone-15-pro-max::before {
    content: '';
    position: absolute;
    top: 50px;
    height: 80px;
}

.iphone-15-pro-max::after {
    content: '';
    position: absolute;
    top: 150px;
    height: 120px;
}

.iphone-15-pro-max .power-button {
    top: 200px;
    height: 80px;
}

.iphone-15-pro-max .screen {
    border-radius: 47px;
}

.iphone-15-pro-max .screen iframe {
    border-radius: 47px;
}

/* iPhone 14 series - similar to 15 but slightly different dimensions */
.iphone-14-pro,
.iphone-14-pro-max {
    background: var(--device-dark);
}

.iphone-14-pro {
    width: 390px;
    height: 844px;
    border-radius: 47px;
    padding: 8px;
}

.iphone-14-pro-max {
    width: 428px;
    height: 926px;
    border-radius: 50px;
    padding: 8px;
}

/* iPhone 13 series */
.iphone-13,
.iphone-13-mini,
.iphone-13-pro,
.iphone-13-pro-max {
    background: var(--device-dark);
    border-radius: 47px;
    padding: 8px;
