.quad-device .screen {
    border-radius: 8px;
}

.quad-device .screen iframe {
    border-radius: 8px;
}

.quad-device .device-label {
    position: absolute;
    top: -30px;
    left: 0;
    color: rgba(255, 255, 255, 0.7);
    font-size: 14px;
    font-weight: 500;
}

/* Trible layout (3 devices) */
.trible-layout {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 50px;
    flex-wrap: wrap;
}

.trible-desktop {
    width: 400px;
    height: 250px;
    background: var(--device-darker);
    border-radius: 12px;
    padding: 8px;
    position: relative;
    box-shadow: var(--shadow-light);
}

.trible-desktop .screen {
    border-radius: var(--border-radius-tiny);
}

.trible-desktop .screen iframe {
    border-radius: var(--border-radius-tiny);
}

.trible-tablet {
    width: 240px;
    height: 320px;
    background: var(--device-dark);
    border-radius: 18px;
    padding: 8px;
    position: relative;
    box-shadow: var(--shadow-light);
}

.trible-tablet .screen {
    border-radius: 10px;
}

.trible-tablet .screen iframe {
    border-radius: 10px;
}

.trible-phone {
    width: 160px;
    height: 320px;
    background: var(--device-dark);
    border-radius: 22px;
    padding: 6px;
    position: relative;
    box-shadow: var(--shadow-light);
}

.trible-phone .screen {
    border-radius: 16px;
}

.trible-phone .screen iframe {
    border-radius: 16px;
}

/* Five layout (5 devices in a cross pattern) */
.five-layout {
    display: grid;
    grid-template-columns: 1fr 1fr 1fr;
    grid-template-rows: 1fr 1fr 1fr;
    gap: 30px;
    width: 100%;
    max-width: 1000px;
}

.five-center {
    grid-column: 2;
    grid-row: 2;
}

.five-top {
    grid-column: 2;
    grid-row: 1;
}

.five-right {
    grid-column: 3;
    grid-row: 2;
}

.five-bottom {
    grid-column: 2;
    grid-row: 3;
}

.five-left {
    grid-column: 1;
    grid-row: 2;
}

.five-device {
    width: 100%;
    aspect-ratio: 16/10;
    background: var(--device-darker);
    border-radius: 12px;
    padding: 8px;
    position: relative;
    box-shadow: var(--shadow-light);
}

.five-device .screen {
    border-radius: 6px;
}

.five-device .screen iframe {
    border-radius: 6px;
}

/* Six layout (2x3 grid) */
.six-layout {
    display: grid;
    grid-template-columns: 1fr 1fr 1fr;
    grid-template-rows: 1fr 1fr;
    gap: 30px;
    width: 100%;
    max-width: 1200px;
}

.six-device {
    width: 100%;
    aspect-ratio: 16/10;
    background: var(--device-darker);
    border-radius: 12px;
    padding: 8px;
    position: relative;
    box-shadow: var(--shadow-light);
}

.six-device .screen {
    border-radius: 6px;
}

.six-device .screen iframe {
    border-radius: 6px;
}

/* Twelve layout (3x4 grid) */
.twelve-layout {
    display: grid;
    grid-template-columns: 1fr 1fr 1fr 1fr;
    grid-template-rows: 1fr 1fr 1fr;
    gap: 20px;
    width: 100%;
    max-width: 1400px;
}

.twelve-device {
    width: 100%;
    aspect-ratio: 16/10;
    background: var(--device-darker);
    border-radius: 10px;
    padding: 6px;
    position: relative;
    box-shadow: var(--shadow-light);
}

.twelve-device .screen {
    border-radius: 5px;
}

.twelve-device .screen iframe {
    border-radius: 5px;
}

/* Layout responsive scaling */
@media (max-width: 1400px) {
    .twelve-layout {
        grid-template-columns: 1fr 1fr 1fr;
        grid-template-rows: 1fr 1fr 1fr 1fr;
    }
}

@media (max-width: 1200px) {
    .responsive-layout,
    .trible-layout {
        flex-direction: column;
        gap: 30px;
    }

    .quad-layout,
    .six-layout {
        grid-template-columns: 1fr;
        grid-template-rows: repeat(auto, 1fr);
    }
}

@media (max-width: 800px) {
    .five-layout {
        grid-template-columns: 1fr;
        grid-template-rows: repeat(5, 1fr);
    }

    .five-center,
    .five-top,
    .five-right,
    .five-bottom,
    .five-left {
        grid-column: 1;
        grid-row: auto;
    }

    .twelve-layout {
        grid-template-columns: 1fr 1fr;
        grid-template-rows: repeat(6, 1fr);
    }
}

@media (max-width: 600px) {
    .twelve-layout {
        grid-template-columns: 1fr;
        grid-template-rows: repeat(12, 1fr);
    }
}

