        content: '';
        position: absolute;
        top: 150px;
        left: -3px;
        width: 6px;
        height: 120px;
        background: #4a4a4c;
        border-radius: 3px 0 0 3px;
    }

    .power-button {
        position: absolute;
        top: 200px;
        right: -3px;
        width: 6px;
        height: 80px;
        background: #4a4a4c;
        border-radius: 0 3px 3px 0;
    }

    .screen {
        width: 100%;
        height: 100%;
        background: #000;
        border-radius: 42px;
        overflow: hidden;
        position: relative;
    }

    .dynamic-island {
        position: absolute;
        top: 15px;
        left: 50%;
        transform: translateX(-50%);
        width: 125px;
        height: 37px;
        background: #000;
        border-radius: 19px;
        z-index: 100;
        box-shadow: inset 0 0 0 1px rgba(255, 255, 255, 0.1);
    }

    .screen iframe {
        width: 100%;
        height: 100%;
        border: none;
        border-radius: 42px;
    }

    @media (max-width: 480px) {
        .iphone-15-pro {
            transform: scale(0.7);
        }
    }

    @media (max-width: 360px) {
        .iphone-15-pro {
            transform: scale(0.6);
        }
    }
</style>
{% endblock %}

