{% extends "@DeviceMockupViewer/storefront/layout/base.html.twig" %}

{% block title %}Smart TV 55" Mockup{% endblock %}

{% block stylesheets %}
<style>
    * {
        margin: 0;
        padding: 0;
        box-sizing: border-box;
    }

    body {
        background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
        min-height: 100vh;
        display: flex;
        align-items: center;
        justify-content: center;
        padding: 20px;
        font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
    }

    .device-container {
        display: flex;
        align-items: center;
        justify-content: center;
        min-height: 100vh;
        width: 100%;
    }

    .smart-tv {
        width: 1200px;
        height: 720px;
        position: relative;
    }

    .tv-frame {
        width: 100%;
        height: 680px;
        background: linear-gradient(145deg, #1a1a1a, #0a0a0a);
        border-radius: 20px;
        padding: 25px;
        position: relative;
        box-shadow: 
            0 0 0 5px #333,
            0 30px 60px rgba(0, 0, 0, 0.5),
            inset 0 2px 4px rgba(255, 255, 255, 0.05);
    }

    .screen {
        width: 100%;
        height: 100%;
        background: #000;
        border-radius: 12px;
        overflow: hidden;
        position: relative;
    }

    .screen iframe {
        width: 100%;
        height: 100%;
        border: none;
        border-radius: 12px;
    }

    .tv-stand {
        width: 300px;
        height: 60px;
        background: linear-gradient(145deg, #333, #222);
        border-radius: 15px;
        position: absolute;
        bottom: 20px;
        left: 50%;
        transform: translateX(-50%);
        box-shadow: 0 8px 20px rgba(0, 0, 0, 0.4);
    }

    .tv-base {
        width: 500px;
        height: 25px;
        background: linear-gradient(145deg, #333, #222);
        border-radius: 50%;
        position: absolute;
        bottom: 0;
        left: 50%;
        transform: translateX(-50%);
        box-shadow: 0 5px 15px rgba(0, 0, 0, 0.5);
    }

    .power-led {
        position: absolute;
        bottom: 35px;
        right: 30px;
        width: 8px;
        height: 8px;
        background: #00ff00;
        border-radius: 50%;
        box-shadow: 0 0 10px #00ff00;
    }

    @media (max-width: 1300px) {
        .smart-tv {
            transform: scale(0.8);
        }
    }

    @media (max-width: 1000px) {
        .smart-tv {
            transform: scale(0.6);
        }
    }

    @media (max-width: 700px) {
        .smart-tv {
            transform: scale(0.4);
        }
    }
</style>
{% endblock %}

{% block content %}
<div class="device-container">
    <div class="smart-tv">
        <div class="tv-frame">
            <div class="screen">
                <iframe src="{{ cleanUrl }}" title="Smart TV 55 Mockup"></iframe>
            </div>
            <div class="power-led"></div>
        </div>
        <div class="tv-stand"></div>
        <div class="tv-base"></div>
    </div>
</div>
{% endblock %}

