{% extends "@DeviceMockupViewer/storefront/layout/base.html.twig" %}

{% block title %}iPhone 12 Mockup{% endblock %}

{% block stylesheets %}
<style>
    * {
        margin: 0;
        padding: 0;
        box-sizing: border-box;
    }

    body {
        background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
        min-height: 100vh;
        display: flex;
        align-items: center;
        justify-content: center;
        padding: 20px;
        font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
    }

    .device-container {
        display: flex;
        align-items: center;
        justify-content: center;
        min-height: 100vh;
        width: 100%;
    }

    .iphone-12 {
        width: 385px;
        height: 844px;
        background: linear-gradient(145deg, #6c9cf7, #5080d4);
        border-radius: 48px;
        padding: 8px;
        position: relative;
        box-shadow: 
            0 0 0 3px #4a80c7,
            0 25px 50px rgba(0, 0, 0, 0.5),
            inset 0 2px 4px rgba(255, 255, 255, 0.2);
    }

    .iphone-12::before {
        content: '';
        position: absolute;
        top: 60px;
        left: -3px;
        width: 6px;
        height: 60px;
        background: #4a80c7;
        border-radius: 3px 0 0 3px;
    }

    .iphone-12::after {
        content: '';
        position: absolute;
        top: 140px;
        left: -3px;
        width: 6px;
        height: 80px;
        background: #4a80c7;
        border-radius: 3px 0 0 3px;
    }

    .power-button {
        position: absolute;
        top: 180px;
        right: -3px;
        width: 6px;
        height: 70px;
        background: #4a80c7;
        border-radius: 0 3px 3px 0;
    }

    .screen {
        width: 100%;
        height: 100%;
        background: #000;
        border-radius: 40px;
        overflow: hidden;
        position: relative;
    }

    .notch {
        position: absolute;
        top: 10px;
        left: 50%;
        transform: translateX(-50%);
        width: 150px;
        height: 30px;
        background: #000;
        border-radius: 0 0 18px 18px;
        z-index: 100;
    }

    .screen iframe {
        width: 100%;
        height: 100%;
        border: none;
        border-radius: 40px;
    }

    @media (max-width: 480px) {
        .iphone-12 {
            transform: scale(0.7);
        }
    }

    @media (max-width: 360px) {
        .iphone-12 {
            transform: scale(0.6);
        }
    }
</style>
{% endblock %}

{% block content %}
<div class="device-container">
    <div class="iphone-12">
        <div class="power-button"></div>
        <div class="screen">
            <div class="notch"></div>
            <iframe src="{{ cleanUrl }}" title="iPhone 12 Mockup"></iframe>
        </div>
    </div>
</div>
{% endblock %}

