        display: flex;
        align-items: center;
        justify-content: center;
        min-height: 100vh;
        width: 100%;
    }

    .iphone-13 {
        width: 385px;
        height: 844px;
        background: linear-gradient(145deg, #ff9a9e, #fecfef);
        border-radius: 48px;
        padding: 8px;
        position: relative;
        box-shadow: 
            0 0 0 3px #f48fb1,
            0 25px 50px rgba(0, 0, 0, 0.5),
            inset 0 2px 4px rgba(255, 255, 255, 0.3);
    }

    .iphone-13::before {
        content: '';
        position: absolute;
        top: 60px;
        left: -3px;
        width: 6px;
        height: 60px;
        background: #f48fb1;
        border-radius: 3px 0 0 3px;
    }

    .iphone-13::after {
        content: '';
        position: absolute;
        top: 140px;
        left: -3px;
        width: 6px;
        height: 80px;
        background: #f48fb1;
        border-radius: 3px 0 0 3px;
    }

    .power-button {
        position: absolute;
        top: 180px;
        right: -3px;
        width: 6px;
        height: 70px;
        background: #f48fb1;
        border-radius: 0 3px 3px 0;
    }

    .screen {
        width: 100%;
        height: 100%;
        background: #000;
        border-radius: 40px;
        overflow: hidden;
        position: relative;
    }

    .notch {
