{% extends "@DeviceMockupViewer/storefront/layout/base.html.twig" %}

{% block title %}iPhone 6 Mockup{% endblock %}

{% block stylesheets %}
    {{ parent() }}
    <style>
        .iphone6-container {
            display: flex;
            align-items: center;
            justify-content: center;
            min-height: 100vh;
            width: 100%;
        }

        .iphone6-mockup {
            width: 375px;
            height: 667px;
            background-image: url('{{ asset('bundles/devicemockupviewer/Images/iphone6.png') }}');
            background-size: contain;
            background-repeat: no-repeat;
            background-position: center;
            position: relative;
            display: flex;
            align-items: center;
            justify-content: center;
        }

        .iphone6-screen {
            width: 320px;
            height: 568px;
            overflow: hidden;
            border-radius: 0;
            position: relative;
            margin-top: -10px;
        }

        .iphone6-screen iframe {
            width: 100%;
            height: 100%;
            border: none;
        }

        @media (max-width: 480px) {
            .iphone6-mockup {
                transform: scale(0.7);
            }
        }

        @media (max-width: 360px) {
            .iphone6-mockup {
                transform: scale(0.6);
            }
        }
    </style>
{% endblock %}

{% block content %}
<div class="iphone6-container">
    <div class="iphone6-mockup">
        <div class="iphone6-screen">
            <iframe src="{{ cleanUrl }}" title="iPhone 6 Mockup"></iframe>
        </div>
    </div>
</div>
{% endblock %}

