file.content:
{% extends "@DeviceMockupViewer/storefront/layout/base.html.twig" %}

{% block title %}Laptop Mockup{% endblock %}

{% block stylesheets %}
<style>
    * {
        margin: 0;
        padding: 0;
        box-sizing: border-box;
    }

    body {
        background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
        min-height: 100vh;
        display: flex;
        align-items: center;
        justify-content: center;
        padding: 20px;
        font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
    }

    .device-container {
        display: flex;
        align-items: center;
        justify-content: center;
        min-height: 100vh;
        width: 100%;
    }

    .laptop {
        width: 950px;
        height: 630px;
        position: relative;
        perspective: 1000px;
    }

    .laptop-screen {
        width: 100%;
        height: 580px;
        background: linear-gradient(145deg, #333, #222);
        border-radius: 12px;
        padding: 12px;
        position: relative;
        transform: rotateX(-5deg);
        box-shadow: 
            0 0 0 2px #555,
            0 25px 50px rgba(0, 0, 0, 0.3),
            inset 0 2px 4px rgba(255, 255, 255, 0.1);
    }

    .screen {
        width: 100%;
        height: 100%;
        background: #000;
        border-radius: 6px;
        overflow: hidden;
        position: relative;
    }

    .screen iframe {
        width: 100%;
        height: 100%;
        border: none;
        border-radius: 6px;
    }

    .laptop-base {
        width: 103%;
        height: 80px;
        background: linear-gradient(145deg, #333, #222);
        border-radius: 0 0 18px 18px;
        position: absolute;
        bottom: -40px;
        left: 50%;
        transform: translateX(-50%);
        box-shadow: 
            0 10px 30px rgba(0, 0, 0, 0.3),
            inset 0 1px 2px rgba(255, 255, 255, 0.1);
    }

    .trackpad {
        width: 150px;
        height: 100px;
        background: rgba(255, 255, 255, 0.05);
        border-radius: 8px;
        position: absolute;
        bottom: 15px;
        left: 50%;
        transform: translateX(-50%);
        border: 1px solid rgba(255, 255, 255, 0.1);
    }

    @media (max-width: 1100px) {
        .laptop {
            transform: scale(0.8);
        }
    }

    @media (max-width: 850px) {
        .laptop {
            transform: scale(0.6);
        }
    }

    @media (max-width: 600px) {
        .laptop {
            transform: scale(0.4);
        }
    }
</style>
{% endblock %}

{% block content %}
<div class="device-container">
    <div class="laptop">
        <div class="laptop-screen">
            <div class="screen">
                <iframe src="{{ cleanUrl }}" title="Laptop Mockup"></iframe>
            </div>
        </div>
        <div class="laptop-base">
            <div class="trackpad"></div>
        </div>
    </div>
</div>
{% endblock %}

