    <style>
        .macbook-container {
            display: flex;
            align-items: center;
            justify-content: center;
            min-height: 100vh;
            width: 100%;
        }

        .macbook-pro-16 {
            width: 800px;
            height: 550px;
            background-image: url('{{ asset('bundles/devicemockupviewer/Images/laptop.png') }}');
            background-size: contain;
            background-repeat: no-repeat;
            background-position: center;
            position: relative;
            display: flex;
            align-items: flex-start;
            justify-content: center;
            padding-top: 45px;
        }

        .macbook-screen {
            width: 580px;
            height: 360px;
            overflow: hidden;
            border-radius: 8px;
            position: relative;
            margin-top: 0;
        }

        .macbook-screen iframe {
            width: 100%;
            height: 100%;
            border: none;
            border-radius: 8px;
        }

        @media (max-width: 1000px) {
            .macbook-pro-16 {
                transform: scale(0.8);
            }
        }

        @media (max-width: 800px) {
            .macbook-pro-16 {
                transform: scale(0.6);
            }
        }

        @media (max-width: 600px) {
