    "macbookpro16:1728:1117"
    "surfacepro:912:1368"
    "surfacelaptop:1536:1024"
    "surfacestudio:2880:1920"
    "steamdeck:800:1280"
    "nintendoswitch:1280:720"
    "smarttv55:1920:1080"
    "smarttv65:3840:2160"
    "monitor4k:3840:2160"
    "appletvscreen:1920:1080"
)

for device_info in "${devices[@]}"; do
    IFS=':' read -r device width height <<< "$device_info"

    # Calculate scale factor for very large devices
    scale_factor="1"
    if [ "$width" -gt "1600" ]; then
        scale_factor="0.5"
    elif [ "$width" -gt "1200" ]; then
        scale_factor="0.7"
    elif [ "$width" -gt "900" ]; then
        scale_factor="0.8"
    fi

    cat > "setup.${device}.css" << EOF
/* ${device} Mockup Setup */
body {
    margin: 0;
    padding: 0;
    background: #f5f5f5;
    min-height: 100vh;
    display: flex;
    align-items: center;
    justify-content: center;
}

#container {
    width: 100%;
    min-height: 100vh;
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 20px;
    box-sizing: border-box;
}

.${device} {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 100%;
    height: 100%;
}

.${device} .mockup {
    width: ${width}px;
    height: ${height}px;
    position: relative;
    border-radius: 25px;
    padding: 20px;
    background: #000;
    box-shadow: 0 8px 30px rgba(0,0,0,0.3);
    transform: scale(${scale_factor});
    transform-origin: center;
}

.${device} .mockup .wrapper {
    width: 100%;
    height: 100%;
    border-radius: 15px;
    overflow: hidden;
    background: #fff;
    position: relative;
}

.${device} .mockup iframe {
    width: 100%;
    height: 100%;
    border: none;
    border-radius: 15px;
    display: block;
}

/* Responsive adjustments */
@media (max-width: 1920px) {
    .${device} .mockup {
        transform: scale(calc(${scale_factor} * 0.9));
    }
}

@media (max-width: 1600px) {
    .${device} .mockup {
        transform: scale(calc(${scale_factor} * 0.8));
    }
}

@media (max-width: 1200px) {
    .${device} .mockup {
        transform: scale(calc(${scale_factor} * 0.7));
    }
}

@media (max-width: 900px) {
    .${device} .mockup {
        transform: scale(calc(${scale_factor} * 0.6));
    }
}

@media (max-width: 600px) {
    .${device} .mockup {
        transform: scale(calc(${scale_factor} * 0.5));
    }
}
EOF
done

