body {
	padding: 0;
	margin: 0;
	font-family: Helvetica;
	color: #444;
}

a {
	color: #fff;
	text-decoration:none
}

h1 {
	font-size: 36px;
	font-weight: bold;
}

h2, h3, h4, h5 {
	font-size: 12px;
	font-weight: normal;
}

