	font-size: 10px;
}

p {
	font-size:16px
}

#mask {
	z-index: 0;
	height: 100vh;
	width: 100vw;
	position: absolute;
	overflow:hidden
}

#container {
	width: 100vw;
	height: 100vh;
	position: absolute;
	left: 0;
	top: 0;
	-webkit-transition-property: top, bottom;
	-webkit-transition-duration: .5s;
	-moz-property: top, bottom;
	-moz-duration: .5s;
	-ms-property: top, bottom;
	-ms-duration: .5s;
	-o-property: top, bottom;
