	transition-property: top, bottom;
	transition-duration:.5s
}

section {
	height: 100vh;
	width: 100vw;
	position:relative
}

.content {
	width: 1000px;
	margin: 0 auto;
	text-align: center;
	padding-top:150px
}

nav ul {
	z-index: 1;
	list-style: none;
	position: fixed;
	right: 10px;
	top:10%
}

nav ul li {
	margin-bottom: 10px;
	height: 100px;
	width: 100px
}

.code {
	margin-top: 15px;
	background: #fff;
	border: 1px dotted #000;
	padding: 5px 0;
	color: #000;
	width:300px
}

.arrowdown {
	width: 0;
	height: 0;
	border-style: solid;
	border-width: 10px 10px 0;
	border-color: #000 transparent transparent;
	cursor:pointer;
	position: fixed;
	bottom: 20px;
	right: 30px;
	z-index: 10;
}

.arrowup {
	width: 0;
	height: 0;
	border-style: solid;
	border-width: 0 10px 10px;
	border-color: transparent transparent #000;
	cursor:pointer;
	position: fixed;
	top: 20px;
	right: 30px;
	z-index: 10;
}

.turquoise-bg {
	background:#1abc9c;
}

.carrot-bg {
	background:#e67e22;
}

.blue-bg {
	background: #2980b9;
}

nav>ul>li {
	transition: all 1s ease;
	opacity: .2;
	width: 60px;
	height: 60px;
	background-repeat: no-repeat;
	background-size: 100%;
	cursor: pointer;
}
nav>ul>li.active {
	opacity: 1;
}
nav>ul>li:hover {
	opacity: .7;
}

xxxsection {
  background-image: url("../Images/background.jpg");
}
section {
  background-color: #F2F2F2;
  background-repeat: no-repeat;
  background-size: 100%;
}



