	color: #000;
	display: block;
	font-weight: bold;
	padding: 0px 0px 0px 0px;
	margin: 0px 0px 0px -100px;
}

nav>ul>li span {
	font-weight: normal;
	padding: 0px 0px 0px 0px;
	margin: 24px 0px 0px -100px;
}

h1, h2 {
	color: #000;
	display: inline;
	margin-left: 10px;
}

.display .header {
	color: #000;
	margin-left: 50%;
	border-left: 1px solid #000;
	padding-top: 40px;
	display: none;
}

.hide {
	display: none;
}

@media (max-width: 1280px) {
	nav>ul>li b { display: none; }
	nav>ul>li span { display: none; }
}

@media (max-width: 768px) {
	.header>h2 { display: none; }
}


