{% extends "@DeviceMockupViewer/storefront/layout/base.html.twig" %}

{% block title %}iPhone 15 Pro Mockup{% endblock %}

{% block stylesheets %}
<style>
    * {
        margin: 0;
        padding: 0;
        box-sizing: border-box;
    }

    body {
        background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
        min-height: 100vh;
        display: flex;
        align-items: center;
        justify-content: center;
        padding: 20px;
        font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
    }

    .device-container {
        display: flex;
        align-items: center;
        justify-content: center;
        min-height: 100vh;
        width: 100%;
    }

    .iphone-15-pro {
        width: 400px;
        height: 870px;
        background: linear-gradient(145deg, #2c2c2e, #1c1c1e);
        border-radius: 50px;
        padding: 8px;
        position: relative;
        box-shadow: 
            0 0 0 3px #4a4a4c,
            0 25px 50px rgba(0, 0, 0, 0.5),
            inset 0 2px 4px rgba(255, 255, 255, 0.1);
    }

    .iphone-15-pro::before {
        content: '';
        position: absolute;
        top: 50px;
        left: -3px;
        width: 6px;
        height: 80px;
        background: #4a4a4c;
        border-radius: 3px 0 0 3px;
    }

    .iphone-15-pro::after {
        content: '';
        position: absolute;
        top: 150px;
        left: -3px;
        width: 6px;
        height: 120px;
        background: #4a4a4c;
        border-radius: 3px 0 0 3px;
    }

    .power-button {
        position: absolute;
        top: 200px;
        right: -3px;
        width: 6px;
        height: 80px;
        background: #4a4a4c;
        border-radius: 0 3px 3px 0;
    }

    .screen {
        width: 100%;
        height: 100%;
        background: #000;
        border-radius: 42px;
        overflow: hidden;
        position: relative;
    }

    .dynamic-island {
        position: absolute;
        top: 15px;
        left: 50%;
        transform: translateX(-50%);
        width: 125px;
        height: 37px;
        background: #000;
        border-radius: 19px;
        z-index: 100;
        box-shadow: inset 0 0 0 1px rgba(255, 255, 255, 0.1);
    }

    .screen iframe {
        width: 100%;
        height: 100%;
        border: none;
        border-radius: 42px;
    }

    @media (max-width: 480px) {
        .iphone-15-pro {
            transform: scale(0.7);
        }
    }

    @media (max-width: 360px) {
        .iphone-15-pro {
            transform: scale(0.6);
        }
    }
</style>
{% endblock %}

{% block content %}
<div class="device-container">
    <div class="iphone-15-pro">
        <div class="power-button"></div>
        <div class="screen">
            <div class="dynamic-island"></div>
            <iframe src="{{ cleanUrl }}" title="iPhone 15 Pro Mockup"></iframe>
        </div>
    </div>
</div>
{% endblock %}

