    }

    .device-container {
        display: flex;
        align-items: center;
        justify-content: center;
        min-height: 100vh;
        width: 100%;
    }

    .macbook-pro-16 {
        width: 1000px;
        height: 650px;
        position: relative;
        perspective: 1000px;
    }

    .macbook-screen {
        width: 100%;
        height: 600px;
        background: linear-gradient(145deg, #2c2c2e, #1c1c1e);
        border-radius: 15px;
        padding: 12px;
        position: relative;
        transform: rotateX(-5deg);
        box-shadow: 
            0 0 0 2px #4a4a4c,
            0 25px 50px rgba(0, 0, 0, 0.3),
            inset 0 2px 4px rgba(255, 255, 255, 0.1);
    }

    .screen {
        width: 100%;
        height: 100%;
        background: #000;
        border-radius: 8px;
        overflow: hidden;
        position: relative;
    }

    .notch {
        position: absolute;
        top: 5px;
        left: 50%;
        transform: translateX(-50%);
        width: 200px;
        height: 28px;
        background: #000;
        border-radius: 0 0 15px 15px;
        z-index: 100;
    }

    .screen iframe {
        width: 100%;
        height: 100%;
        border: none;
        border-radius: 8px;
    }

    .macbook-base {
        width: 105%;
        height: 80px;
        background: linear-gradient(145deg, #2c2c2e, #1c1c1e);
        border-radius: 0 0 20px 20px;
        position: absolute;
        bottom: -40px;
        left: 50%;
        transform: translateX(-50%);
        box-shadow: 
            0 10px 30px rgba(0, 0, 0, 0.3),
            inset 0 1px 2px rgba(255, 255, 255, 0.1);
    }

    .trackpad {
        width: 150px;
        height: 100px;
        background: rgba(255, 255, 255, 0.05);
        border-radius: 8px;
        position: absolute;
        bottom: 15px;
        left: 50%;
        transform: translateX(-50%);
        border: 1px solid rgba(255, 255, 255, 0.1);
    }

    @media (max-width: 1200px) {
        .macbook-pro-16 {
            transform: scale(0.8);
        }
    }

    @media (max-width: 900px) {
        .macbook-pro-16 {
            transform: scale(0.6);
        }
    }

    @media (max-width: 600px) {
        .macbook-pro-16 {
            transform: scale(0.4);
        }
    }
