{% extends "@DeviceMockupViewer/storefront/layout/base.html.twig" %}

{% block title %}iPad Pro 13" Mockup{% endblock %}

{% block stylesheets %}
<style>
    * {
        margin: 0;
        padding: 0;
        box-sizing: border-box;
    }

    body {
        background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
        min-height: 100vh;
        display: flex;
        align-items: center;
        justify-content: center;
        padding: 20px;
        font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
    }

    .device-container {
        display: flex;
        align-items: center;
        justify-content: center;
        min-height: 100vh;
        width: 100%;
    }

    .ipad-pro-13 {
        width: 700px;
        height: 950px;
        background: linear-gradient(145deg, #2c2c2e, #1c1c1e);
        border-radius: 25px;
        padding: 15px;
        position: relative;
        box-shadow: 
            0 0 0 3px #4a4a4c,
            0 25px 50px rgba(0, 0, 0, 0.4),
            inset 0 2px 4px rgba(255, 255, 255, 0.1);
    }

    .screen {
        width: 100%;
        height: 100%;
        background: #000;
        border-radius: 15px;
        overflow: hidden;
        position: relative;
    }

    .camera-bump {
        position: absolute;
        top: 25px;
        left: 50%;
        transform: translateX(-50%);
        width: 80px;
        height: 25px;
        background: #1c1c1e;
        border-radius: 12px;
        z-index: 100;
        display: flex;
        align-items: center;
        justify-content: center;
    }

    .camera-lens {
        width: 8px;
        height: 8px;
        background: #333;
        border-radius: 50%;
        margin: 0 2px;
    }

    .home-indicator {
        position: absolute;
        bottom: 10px;
        left: 50%;
        transform: translateX(-50%);
        width: 140px;
        height: 5px;
        background: rgba(255, 255, 255, 0.3);
        border-radius: 3px;
        z-index: 100;
    }

    .screen iframe {
        width: 100%;
        height: 100%;
        border: none;
        border-radius: 15px;
    }

    @media (max-width: 800px) {
        .ipad-pro-13 {
            transform: scale(0.7);
        }
    }

    @media (max-width: 600px) {
        .ipad-pro-13 {
            transform: scale(0.5);
        }
    }

    @media (max-width: 400px) {
        .ipad-pro-13 {
            transform: scale(0.4);
        }
    }
</style>
{% endblock %}

{% block content %}
<div class="device-container">
    <div class="ipad-pro-13">
        <div class="screen">
            <div class="camera-bump">
                <div class="camera-lens"></div>
                <div class="camera-lens"></div>
            </div>
            <div class="home-indicator"></div>
            <iframe src="{{ cleanUrl }}" title="iPad Pro 13 Mockup"></iframe>
        </div>
    </div>
</div>
{% endblock %}

