    }

    .laptop-screen {
        width: 100%;
        height: 580px;
        background: linear-gradient(145deg, #333, #222);
        border-radius: 12px;
        padding: 12px;
        position: relative;
        transform: rotateX(-5deg);
        box-shadow: 
            0 0 0 2px #555,
            0 25px 50px rgba(0, 0, 0, 0.3),
            inset 0 2px 4px rgba(255, 255, 255, 0.1);
    }

    .screen {
        width: 100%;
        height: 100%;
        background: #000;
        border-radius: 6px;
        overflow: hidden;
        position: relative;
    }

    .screen iframe {
        width: 100%;
        height: 100%;
        border: none;
        border-radius: 6px;
    }

    .laptop-base {
        width: 103%;
        height: 80px;
        background: linear-gradient(145deg, #333, #222);
        border-radius: 0 0 18px 18px;
        position: absolute;
        bottom: -40px;
        left: 50%;
