14	use App\Kernel;
15	use Sulu\Component\HttpKernel\SuluKernel;
16	use Symfony\Component\Dotenv\Dotenv;
17	use Symfony\Component\ErrorHandler\Debug;
18	use Symfony\Component\HttpFoundation\Request;
19	
20	\defined('SULU_MAINTENANCE') || \define('SULU_MAINTENANCE', \getenv('SULU_MAINTENANCE') ?: false);
21	
22	// maintenance mode
23	if (SULU_MAINTENANCE) {
24	    $maintenanceFilePath = __DIR__ . '/maintenance.php';
25	    // show maintenance mode and exit if no allowed IP is met
26	    if (require $maintenanceFilePath) {
27	        exit;
28	    }
29	}
30	
31	require \dirname(__DIR__) . '/vendor/autoload.php';
32	
33	(new Dotenv())->bootEnv(\dirname(__DIR__) . '/.env');
34	
35	if ($_SERVER['APP_DEBUG']) {
36	    \umask(0000);
37	
38	    Debug::enable();
39	}
40	
41	if ($trustedProxies = $_SERVER['TRUSTED_PROXIES'] ?? false) {
42	    Request::setTrustedProxies(\explode(',', $trustedProxies), Request::HEADER_X_FORWARDED_FOR | Request::HEADER_X_FORWARDED_PORT | Request::HEADER_X_FORWARDED_PROTO);
43	}
44	
45	if ($trustedHosts = $_SERVER['TRUSTED_HOSTS'] ?? false) {
46	    Request::setTrustedHosts([$trustedHosts]);
47	}
48	
49	$suluContext = SuluKernel::CONTEXT_WEBSITE;
50	
51	if (\preg_match('/^\/admin(\/|$)/', $_SERVER['REQUEST_URI'])) {
52	    $suluContext = SuluKernel::CONTEXT_ADMIN;
53	}
54	
55	$kernel = new Kernel($_SERVER['APP_ENV'], (bool) $_SERVER['APP_DEBUG'], $suluContext);
56	
57	// Comment this line if you want to use the "varnish" http
58	// caching strategy. See http://sulu.readthedocs.org/en/latest/cookbook/caching-with-varnish.html
59	if ('dev' !== $_SERVER['APP_ENV'] && SuluKernel::CONTEXT_WEBSITE === $suluContext) {
60	    $kernel = $kernel->getHttpCache();
61	}
62	
63	// When using the HttpCache, you need to call the method in your front controller
64	// instead of relying on the configuration parameter
65	// https://symfony.com/doc/3.4/reference/configuration/framework.html#http-method-override
66	Request::enableHttpMethodParameterOverride();
67	$request = Request::createFromGlobals();
68	$response = $kernel->handle($request);
69	$response->send();
70	$kernel->terminate($request, $response);
